import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function CreateExercise() {
  const [form, setVal] = useState({
    username: "",
    description: "",
    duration: 0,
    date: new Date(),
    users: [],
  });

  const update = (e) => {
    setVal({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const exercise = {
      username: form.username,
      description: form.description,
      duration: form.duration,
      date: form.date,
    };
    console.log(exercise);
    axios
      .post("http://localhost:5000/exercises/add", exercise)
      .then((res) => console.log(res.data));
  };

  useEffect(() => {
    axios.get("http://localhost5000/users").then();
    setVal({ ...form, users: ["User1"], username: "user1" });
  }, []);

  return (
    <>
      <div>
        <h3>Create New Exercise Log</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Username: </label>
            <select
              required
              className="form-control"
              value={form.username}
              onChange={(e) => setVal({ ...form, username: e.target.value })}
            >
              {form.users.map((user) => {
                return (
                  <option key={user} value={user}>
                    {user}
                  </option>
                );
              })}
            </select>
          </div>
          <div className="form-group">
            <label>Description: </label>
            <input
              type="text"
              required
              className="form-control"
              value={form.description}
              onChange={(e) => setVal({ ...form, description: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label>Duration (in minutes): </label>
            <input
              type="number"
              className="form-control"
              value={form.duration}
              onChange={(e) => setVal({ ...form, duration: e.target.value })}
            />
          </div>
          <div className="form-group">
            <label>Date: </label>
            <input
              type="date"
              selected={form.date}
              onChange={(e) => setVal({ ...form, date: e.target.value })}
            />
          </div>

          <div className="form-group">
            <button
              type="submit"
              disabled={form.description.length < 1}
              className="btn btn-primary"
            >
              Create Exercise Log
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

export default CreateExercise;
