import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ExercisesList() {
  const [exercises, setExercise] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/exercises/")
      .then((response) => {
        setExercise(response.data); // Adds the list of exercises to the state
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const deleteExercise = (id) => {
    axios
      .delete("http://localhost:5000/exercises/" + id)
      .then((response) => console.log(response.data));
    setExercise({
      exercises: exercises.filter((element) => element._id !== id), // "_id" is from the id displayed in the MondoDB database
    });
  };

  return (
    <>
      <div>
        <p>You are on the Exercises List page</p>
      </div>
    </>
  );
}

export default ExercisesList;
