import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

function ExercisesList() {
  const [exercises, setExercise] = useState([]);
  const [delExercise, setDelExercise] = useState();

  const Exercise = (props) => (
    <tr>
      <td>{props.exercise.username}</td>
      <td>{props.exercise.description}</td>
      <td>{props.exercise.duration}</td>
      <td>{props.exercise.date.substring(0, 10)}</td>
      <td>
        <Link to={"/edit/" + props.exercise._id}>edit</Link> |{" "}
        <a
          href="#"
          onClick={() => {
            deleteExercise(props.exercise._id);
          }}
        >
          delete
        </a>
      </td>
    </tr>
  );

  useEffect(() => {
    axios
      .get("http://localhost:5000/exercises/")
      .then((response) => {
        setExercise(response.data); // Adds the list of exercises to the state
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const deleteExercise = (id) => {
    axios
      .delete("http://localhost:5000/exercises/" + id)
      .then((response) => console.log(response.data));
    setExercise({
      exercises: exercises.filter((element) => element._id !== id), // "_id" is from the id displayed in the MondoDB database
    });
  };

  const exerciseList = ({ exercises, deleteExercise }) => {
    return (
      <ul>
        {exercises.map((currentExercise) => {
          return (
            <Exercise
              exercise={currentExercise}
              deleteExercise={deleteExercise}
              key={currentExercise._id}
            />
          );
        })}
      </ul>
    );
  };

  // const exerciseList = () => {
  //   return exercises.map((currentExercise) => {
  //     return (
  //       <Exercise
  //         exercise={currentExercise}
  //         deleteExercise={deleteExercise}
  //         key={currentExercise._id}
  //       />
  //     );
  //   });
  // };

  return (
    <>
      <div>
        <h3>Logged Exercises</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Description</th>
              <th>Duration</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{exerciseList}</tbody>
        </table>
      </div>
    </>
  );
}

export default ExercisesList;
